//Return true if all numbers in the array l are below threshold t.
// >>> below_threshold([1, 2, 4, 10], 100)
// true
// >>> below_threshold([1, 20, 4, 10], 5)
// false
function below_threshold(l, t){
    for(let i=0; i<l.length; i++){
        if(l[i] >= t){
            return false;
        }
    }
    return true;
}
