// 55
// >>> fib(1)
// 1
// >>> fib(8)
// 21
function fib(n){
    if(n<=2) return 1;
    let fibNums = [1,1];
    for(let i=2; i<n; i++){
        fibNums[i] = fibNums[i-1] + fibNums[i-2];
    }
    return fibNums[n-1];
}
