function fibfib(n){
    if(n === 0) return 0;
    if(n === 1) return 0;
    if(n === 2) return 1;
    let fibfibNums = [0, 0, 1];
    for(let i = 3; i <= n; i++){
        fibfibNums[i] = fibfibNums[i-1] + fibfibNums[i-2] + fibfibNums[i-3];
    }
    return fibfibNums[n];
}
