//Circular shift the digits of the integer x, shift the digits right by shift
// and return the result as a string.
// If shift > number of digits, return digits reversed.
// >>> circular_shift(12, 1)
// "21"
// >>> circular_shift(12, 2)
// "12"
function circular_shift(x, shift){
    let digits = x.toString().split('');
    let len = digits.length;
    if(shift > len){
        return digits.reverse().join('');
    }
    else{
        for(let i = 0; i < shift; i++){
            let last = digits.pop();
            digits.unshift(last);
        }
        return digits.join('');
    }
}

//Circular shift the digits of the integer x, shift the digits right by shift
// and return the result as a string.
// If shift > number of digits, return digits reversed.
// >>> circular_shift(12, 1)
// "21"
// >>> circular_shift(12, 2)
// "12"
function circular_shift(x, shift){
    let str = x.toString();
    let len = str.length;
    if(shift > len){
        return str.split('').reverse().join('');
    }
    else{
        return str.slice(shift) + str.slice(0, shift);
    }
}

//Circular shift the digits of the integer x, shift the digits right by shift
// and return the result as a string.
// If shift > number of digits, return digits reversed.
// >>> circular_shift(12, 1)
// "21"
// >>> circular_shift(12, 2)
// "12"
function circular_shift(x, shift){
    let digits = x.toString().split('');
    let len = digits.length;
    if(shift > len){
        return digits.reverse().join('');
    }
    else{
        let shifted = digits.splice(len - shift, len);
        return (shifted.concat(digits)).join('');
    }
}

