// "12"
function circular_shift(x, shift){
    let str = x.toString();
    let len = str.length;
    if(shift > len){
        return str.split("").reverse().join("");
    }
    else{
        let part1 = str.slice(0, len - shift);
        let part2 = str.slice(len - shift);
        return part2 + part1;
    }
}

