//You are given an array of integers.
// Write a function next_smallest() that returns the 2nd smallest element of the array.
// Return undefined if there is no such element.
// >>> next_smallest([1, 2, 3, 4, 5])
// 2
// >>> next_smallest([5, 1, 4, 3, 2])
// 2
// >>> next_smallest([])
// undefined
// >>> next_smallest([1, 1])
// undefined
function next_smallest(lst){
    let smallest = Infinity;
    let second_smallest = Infinity;
    for(let i = 0; i < lst.length; i++){
        if(lst[i] < smallest){
            second_smallest = smallest;
            smallest = lst[i];
        }else if(lst[i] < second_smallest && lst[i] != smallest){
            second_smallest = lst[i];
        }
    }
    if(second_smallest == Infinity){
        return undefined;
    }else{
        return second_smallest;
    }
}

