// or the sum of numbers from 1 to i otherwise.
// i starts from 1.
// the factorial of i is the multiplication of the numbers from 1 to i (1 * 2 * ... * i).
// Example:
// >>> f(5)
// [1, 2, 6, 24, 15]
function f(n){
    let arr = [];
    for(let i = 1; i <= n; i++){
        if(i % 2 === 0){
            let fact = 1;
            for(let j = 1; j <= i; j++){
                fact *= j;
            }
            arr.push(fact);
        } else {
            let sum = 0;
            for(let j = 1; j <= i; j++){
                sum += j;
            }
            arr.push(sum);
        }
    }
    return arr;
}
