// Note:
// 1. 1 <= n <= 10^3
// 2. returned array has the number of even and odd integer palindromes respectively.
function even_odd_palindrome(n){
    let even = 0, odd = 0;
    for(let i = 1; i <= n; i++){
        if(i.toString() === i.toString().split('').reverse().join('')){
            if(i % 2 === 0) even++;
            else odd++;
        }
    }
