// {"a": 2, "b": 2}
// >>> histogram("b b b b a")
// {"b": 4}
// >>> histogram("")
// {}
function histogram(test){
    let count = {};
    let max = 0;
    let result = {};
    let arr = test.split(' ');
    for(let i = 0; i < arr.length; i++){
        if(count[arr[i]]){
            count[arr[i]]++;
        } else {
            count[arr[i]] = 1;
        }
        if(count[arr[i]] > max){
            max = count[arr[i]];
        }
    }
    for(let key in count){
        if(count[key] === max){
            result[key] = max;
        }
    }
    return result;
}

