// {"a": 2, "b": 2}
// >>> histogram("b b b b a")
// {"b": 4}
// >>> histogram("")
// {}
function histogram(test){
    var count = {};
    var arr = test.split(' ');
    var max = 0;
    for(var i = 0; i < arr.length; i++){
        if(count[arr[i]]){
            count[arr[i]]++;
        } else {
            count[arr[i]] = 1;
