// Return 'Yes' if there's a way to make a good string, and return 'No' otherwise.
// Examples:
// >>> match_parens(["()(", ")"])
// "Yes"
// >>> match_parens([")", ")"])
// "No"
function match_parens(lst){
    let open = 0, close = 0;
    for(let i = 0; i < lst[0].length; i++){
        if(lst[0][i] === '(') open++;
        else close++;
    }
    for(let i = 0; i < lst[1].length; i++){
        if(lst[1][i] === '(') open++;
        else close++;
    }
    return (open === close) ? "Yes" : "No";
}

//You are given an array of two strings, both strings consist of open
// parentheses '(' or close parentheses ')' only.
// Your job is to check if it is possible to concatenate the two strings in
// some order, that the resulting string will be good.
// A string S is considered to be good if and only if all parentheses in S
// are balanced. For example: the string '(())()' is good, while the string
// '())' is not.
// Return 'Yes' if there's a way to make a good string, and return 'No' otherwise.
// Examples:
// >>> match_parens(["()(", ")"])
// "Yes"
// >>> match_parens([")", ")"])
// "No"
function match_parens(lst){
    let open = 0;
    let close = 0;
    for(let i = 0; i < lst[0].length; i++){
        if(lst[0][i] === '('){
            open++;
        }else{
            close++;
        }
    }
    for(let i = 0; i < lst[1].length; i++){
        if(lst[1][i] === '('){
            open++;
        }else{
            close++;
        }
    }
    if(open === close){
        return 'Yes';
    }else{
        return 'No';
    }
}

//Given an array arr of integers and a positive integer k, return a sorted array 
// of length k with the maximum k numbers in arr.
// Example 1:
// >>> maximum([-3, -4, 5], 3)
// [-4, -3, 5]
// Example 2:
// >>> maximum([4, -4, 4], 2)
// [4, 4]
// Example 3:
// >>> maximum([-3, 2, 1, 2, -1, -2, 1], 1)
// [2]
// Note:
// 1. The length of the array will be in the range of [1, 1000].
// 2. The elements in the array will be in the range of [-1000, 1000].
// 3. 0 <= k <= len(arr)
function maximum(arr, k){
    let sortedArr = arr.sort((a, b) => b - a);
    return sortedArr.slice(0, k);
}
