//Given a positive integer n, return the product of the odd digits.
// Return 0 if all digits are even.
// For example:
// >>> digits(1)
// 1
// >>> digits(4)
// 0
// >>> digits(235)
// 15
function digits(n){
    let product = 1;
    let isOdd = false;
    while(n){
        let digit = n % 10;
        if(digit % 2 !== 0){
            product *= digit;
            isOdd = true;
        }
        n = Math.floor(n / 10);
    }
    return isOdd ? product : 0;
}

