// false
// >>> is_nested("[][]")
// false
// >>> is_nested("[]")
// false
// >>> is_nested("[[][]]")
// true
// >>> is_nested("[[]][[")
// true
function is_nested(string){
    let stack = [];
    for(let i = 0; i < string.length; i++){
        if(string[i] === '['){
            stack.push(string[i]);
        } else {
            if(stack.length === 0){
                return false;
            } else {
                stack.pop();
            }
        }
    }
    return stack.length === 0 ? false : true;
}

