    if(txt.length === 0){
        return false;
    }
    let lastChar = txt.charAt(txt.length - 1);
    if(lastChar.match(/[a-z]/i)){
        if(txt.length === 1 || txt.charAt(txt.length - 2) === ' '){
            return true;
        }
    }
    return false;
}

