// Return undefined if the values are equal.
// Note: If a real number is represented as a string, the floating point might be . or ,
// >>> compare_one(1, 2.5)
// 2.5
// >>> compare_one(1, "2,3")
// "2,3"
// >>> compare_one("5,1", "6")
// "6"
// >>> compare_one("1", 1)
// undefined
function compare_one(a, b){
    if(typeof a === 'string'){
        a = parseFloat(a.replace(',', '.'));
    }
    if(typeof b === 'string'){
        b = parseFloat(b.replace(',', '.'));
    }
    if(a > b){
        return a;
    } else if(b > a){
        return b;
    } else {
        return undefined;
    }
}

