// >>> is_equal_to_sum_even(6)
// false
// >>> is_equal_to_sum_even(8)
// true
function is_equal_to_sum_even(n){
    if(n % 2 != 0){
        return false;
    }
    for(let i = 2; i <= n/2; i += 2){
        if(i % 2 == 0){
            let diff = n - i;
            if(diff % 2 == 0){
                return true;
            }
        }
    }
    return false;
}

