//Evaluate whether the given number n can be written as the sum of exactly 4 positive even numbers
// Example
// >>> is_equal_to_sum_even(4)
// false
// >>> is_equal_to_sum_even(6)
// false
// >>> is_equal_to_sum_even(8)
// true
function is_equal_to_sum_even(n){
    if(n % 2 !== 0) return false;
    for(let i = 2; i <= n; i += 2){
        if(i % 2 !== 0) continue;
        let diff = n - i;
        for(let j = i + 2; j <= diff; j += 2){
            if(j % 2 !== 0) continue;
            let diff2 = diff - j;
            for(let k = j + 2; k <= diff2; k += 2){
                if(k % 2 !== 0) continue;
                let diff3 = diff2 - k;
                if(diff3 % 2 === 0 && diff3 >= 2) return true;
            }
        }
    }
    return false;
}

