    let count = 0;
    let dot_index = file_name.indexOf('.');
    let first_part = file_name.slice(0, dot_index);
    let second_part = file_name.slice(dot_index + 1);
    for(let i = 0; i < file_name.length; i++){
        if(isNaN(file_name[i]) === false){
            count++;
        }
