    if(count > 3 || dot_index === -1 || first_part.length === 0 ||
    (first_part[0] < 'A' || (first_part[0] > 'Z' && first_part[0] < 'a') || first_part[0] > 'z') ||
    (second_part !== 'txt' && second_part !== 'exe' && second_part !== 'dll')){
        return 'No';
    }
