//Your task is to implement a function that will simplify the expression
// x * n. The function returns true if x * n evaluates to a whole number and false
// otherwise. Both x and n, are string representation of a fraction, and have the following format,
// <numerator>/<denominator> where both numerator and denominator are positive whole numbers.
// You can assume that x, and n are valid fractions, and do not have zero as denominator.
// >>> simplify("1/5", "5/1")
// true
// >>> simplify("1/6", "2/1")
// false
// >>> simplify("7/10", "10/2")
// false
function simplify(x, n){
    let xArr = x.split('/');
    let nArr = n.split('/');
    let xNum = parseInt(xArr[0]);
    let xDen = parseInt(xArr[1]);
    let nNum = parseInt(nArr[0]);
    let nDen = parseInt(nArr[1]);
    let resultNum = xNum * nNum;
    let resultDen = xDen * nDen;
    if(resultNum % resultDen === 0){
        return true;
    }
    return false;
}

//Your task is to implement a function that will simplify the expression
// x * n. The function returns true if x * n evaluates to a whole number and false
// otherwise. Both x and n, are string representation of a fraction, and have the following format,
// <numerator>/<denominator> where both numerator and denominator are positive whole numbers.
// You can assume that x, and n are valid fractions, and do not have zero as denominator.
// >>> simplify("1/5", "5/1")
// true
// >>> simplify("1/6", "2/1")
// false
// >>> simplify("7/10", "10/2")
// false
function simplify(x, n){
    let xNum = parseInt(x.split("/")[0]);
    let xDen = parseInt(x.split("/")[1]);
    let nNum = parseInt(n.split("/")[0]);
    let nDen = parseInt(n.split("/")[1]);
    return (xNum * nDen) === (xDen * nNum);
}

