// >>> order_by_points([1, 11, -1, -11, -12])
// [-1, -11, 1, -12, 11]
// >>> order_by_points([])
// []
function order_by_points(nums){
    let arr = nums.map((num, index) => {
        let sum = num.toString().split('').reduce((a, b) => parseInt(a) + parseInt(b), 0);
        return {num: num, sum: sum, index: index};
    });
    arr.sort((a, b) => a.sum - b.sum || a.index - b.index);
    return arr.map(obj => obj.num);
