//Write a function which sorts the given array of integers
// in ascending order according to the sum of their digits.
// Note: if there are several items with similar sum of their digits,
// order them based on their index in original array.
// For example:
// >>> order_by_points([1, 11, -1, -11, -12])
// [-1, -11, 1, -12, 11]
// >>> order_by_points([])
// []
function order_by_points(nums){
    let points = nums.map(num => {
        let sum = 0;
        let str = Math.abs(num).toString();
        for(let i = 0; i < str.length; i++){
            sum += parseInt(str[i]);
        }
        return sum;
    });
    let result = [];
    for(let i = 0; i < points.length; i++){
        let min = Math.min(...points);
        let index = points.indexOf(min);
        result.push(nums[index]);
        points[index] = Infinity;
    }
    return result;
}
