//There are eight planets in our solar system: the closerst to the Sun 
// is Mercury, the next one is Venus, then Earth, Mars, Jupiter, Saturn, 
// Uranus, Neptune.
// Write a function that takes two planet names as strings planet1 and planet2. 
// The function should return an array containing all planets whose orbits are 
// located between the orbit of planet1 and the orbit of planet2, sorted by 
// the proximity to the sun. 
// The function should return an empty array if planet1 or planet2
// are not correct planet names. 
// Examples
// >>> bf("Jupiter", "Neptune")
// ["Saturn", "Uranus"]
// >>> bf("Earth", "Mercury")
// "Venus"
// >>> bf("Mercury", "Uranus")
// ["Venus", "Earth", "Mars", "Jupiter", "Saturn"]
function bf(planet1, planet2){
    const planets = ["Mercury", "Venus", "Earth", "Mars", "Jupiter", "Saturn", "Uranus", "Neptune"];
    let result = [];
    if(planets.includes(planet1) && planets.includes(planet2)){
        let index1 = planets.indexOf(planet1);
        let index2 = planets.indexOf(planet2);
        if(index1 < index2){
            result = planets.slice(index1+1, index2);
        }else{
            result = planets.slice(index2+1, index1);
        }
    }
    return result;
}

