//This function takes an array l and returns an array l' such that
// l' is identical to l in the indicies that are not divisible by three, while its values at the indicies that are divisible by three are equal
// to the values of the corresponding indicies of l, but sorted.
// >>> sort_third([1, 2, 3])
// [1, 2, 3]
// >>> sort_third([5, 6, 3, 4, 8, 9, 2])
// [2, 6, 3, 4, 8, 9, 5]
function sort_third(l){
    let l_prime = [...l];
    let divisible_by_three = l.filter((_, index) => index % 3 === 0);
    divisible_by_three.sort((a, b) => a - b);
    for(let i = 0; i < l.length; i++){
        if(i % 3 === 0){
            l_prime[i] = divisible_by_three.shift();
        }
    }
    return l_prime;
}
//This function takes an array l and returns an array l' such that
// l' is identical to l in the indicies that are not divisible by three, while its values at the indicies that are divisible by three are equal
// to the values of the corresponding indicies of l, but sorted.
// >>> sort_third([1, 2, 3])
// [1, 2, 3]
// >>> sort_third([5, 6, 3, 4, 8, 9, 2])
// [2, 6, 3, 4, 8, 9, 5]
function sort_third(l){
    let arr = [...l];
    let divisibleByThree = arr.filter((_, i) => i % 3 === 0).sort((a, b) => a - b);
    for(let i = 0; i < arr.length; i++){
        if(i % 3 === 0){
            arr[i] = divisibleByThree.shift();
        }
    }
    return arr;
}
