//Return the number of times the digit 7 appears in integers less than n which are divisible by 11 or 13.
// >>> fizz_buzz(50)
// 0
// >>> fizz_buzz(78)
// 2
// >>> fizz_buzz(79)
// 3
function fizz_buzz(n){
    let count = 0;
    for(let i = 11; i < n; i++){
        if(i % 11 === 0 || i % 13 === 0){
            count += (i.toString().match(/7/g) || []).length;
        }
    }
    return count;
}

