    let even_values = l.filter((value, index) => index % 2 === 0).sort((a, b) => a - b);
    let result = [];
    for(let i = 0; i < l.length; i++){
        if(i % 2 === 0){
            result.push(even_values.shift());
        } else {
            result.push(l[i]);
        }
    }
    return result;
}
