// false
// >>> same_chars("abcd", "dddddddabce")
// false
// >>> same_chars("eabcdzzzz", "dddzzzzzzzddddabc")
// false
function same_chars(s0, s1){
    let count = new Map();
    for(let char of s0){
        if(count.has(char)){
            count.set(char, count.get(char) + 1);
        }else{
            count.set(char, 1);
        }
    }
    for(let char of s1){
        if(count.has(char)){
            count.set(char, count.get(char) - 1);
        }else{
            return false;
        }
    }
    for(let value of count.values()){
        if(value != 0){
            return false;
        }
    }
    return true;
}
