//Return true is array elements are monotonically increasing or decreasing.
// >>> monotonic([1, 2, 4, 20])
// true
// >>> monotonic([1, 20, 4, 10])
// false
// >>> monotonic([4, 1, 0, -10])
// true
function monotonic(l){
    let increasing = true;
    let decreasing = true;
    for(let i=0; i<l.length-1; i++){
        if(l[i]>l[i+1]) increasing = false;
        if(l[i]<l[i+1]) decreasing = false;
    }
    return increasing || decreasing;
}

//Return true is array elements are monotonically increasing or decreasing.
// >>> monotonic([1, 2, 4, 20])
// true
// >>> monotonic([1, 20, 4, 10])
// false
// >>> monotonic([4, 1, 0, -10])
// true
function monotonic(l){
    let increasing = true;
    let decreasing = true;
    for(let i = 0; i < l.length - 1; i++){
        if(l[i] > l[i+1]){
            increasing = false;
        }
        if(l[i] < l[i+1]){
            decreasing = false;
        }
    }
    return increasing || decreasing;
}

//Return true is array elements are monotonically increasing or decreasing.
// >>> monotonic([1, 2, 4, 20])
// true
// >>> monotonic([1, 20, 4, 10])
// false
// >>> monotonic([4, 1, 0, -10])
// true
function monotonic(l){
    return (l.every((v, i) => i === 0 || v >= l[i - 1])) || (l.every((v, i) => i === 0 || v <= l[i - 1]));
}

