//Given a positive integer N, return the total sum of its digits in binary.
// Example
// >>> solve(1000)
// "1"
// >>> solve(150)
// "110"
// >>> solve(147)
// "1100"
// Variables:
// @N integer
// Constraints: 0 ≤ N ≤ 10000.
// Output:
// a string of binary number
function solve(N){
    let binary = N.toString(2);
    let sum = 0;
    for(let i = 0; i < binary.length; i++){
        sum += parseInt(binary[i]);
    }
    return sum.toString();
}
//Given a positive integer N, return the total sum of its digits in binary.
// Example
// >>> solve(1000)
// "1"
// >>> solve(150)
// "110"
// >>> solve(147)
// "1100"
// Variables:
// @N integer
// Constraints: 0 ≤ N ≤ 10000.
// Output:
// a string of binary number
function solve(N){
    let sum = 0;
    while(N > 0){
        sum += N % 2;
        N = Math.floor(N / 2);
    }
    return sum.toString();
}
