//Given a non-empty array of integers lst. add the even elements that are at odd indices..
// Examples:
// >>> add([4, 2, 6, 7])
// 2
function add(lst){
    let sum = 0;
    for(let i=1; i<lst.length; i+=2){
        if(lst[i] % 2 === 0){
            sum += lst[i];
        }
    }
    return sum;
}

//Given a non-empty array of integers lst. add the even elements that are at odd indices..
// Examples:
// >>> add([4, 2, 6, 7])
// 2
function add(lst){
    let sum = 0;
    for(let i = 0; i < lst.length; i++){
        if(i % 2 !== 0 && lst[i] % 2 === 0){
            sum += lst[i];
        }
    }
    return sum;
}

