// >>> count_upper("aBCdEf")
// 1
// >>> count_upper("abcdefg")
// 0
// >>> count_upper("dBBE")
// 0
function count_upper(s){
    let count = 0;
    for(let i = 0; i < s.length; i+=2){
        if(s[i] === s[i].toUpperCase() && "AEIOU".includes(s[i])){
            count++;
        }
    }
    return count;
}

