// 3. 0 <= k <= len(arr)
function maximum(arr, k){
    let maxHeap = new MaxPriorityQueue();
    for(let num of arr){
        maxHeap.enqueue(num);
        if(maxHeap.size() > k){
            maxHeap.dequeue();
        }
    }
    let result = [];
    while(!maxHeap.isEmpty()){
        result.push(maxHeap.dequeue().element);
    }
    return result.sort((a, b) => b - a);
}
