    let collatz = [n];
    while(n != 1){
        if(n % 2 == 0){
            n = n / 2;
        }else{
            n = 3 * n + 1;
        }
        collatz.push(n);
    }
    let odd_collatz = collatz.filter(num => num % 2 != 0);
