// >>> file_name_check("1example.dll")
// "No"
function file_name_check(file_name){
    let count = 0;
    for(let i = 0; i < file_name.length; i++){
        if(isNaN(file_name[i]) === false){
            count++;
        }
    }
    if(count > 3){
        return "No";
    }
    let dot_index = file_name.indexOf('.');
    if(dot_index === -1 || dot_index === 0 || dot_index === file_name.length - 1){
        return "No";
    }
    let prefix = file_name.slice(0, dot_index);
    let suffix = file_name.slice(dot_index + 1);
    if(isNaN(prefix[0]) === true || prefix[0] < 'A' || (prefix[0] > 'Z' && prefix[0] < 'a') || prefix[0] > 'z'){
        return "No";
    }
    if(suffix !== 'txt' && suffix !== 'exe' && suffix !== 'dll'){
        return "No";
    }
    return "Yes";
}

