// 1
// >>> specialFilter([33, -2, -3, 45, 21, 109])
// 2
function specialFilter(nums){
    let count = 0;
    for(let i = 0; i < nums.length; i++){
        let num = Math.abs(nums[i]);
        if(num > 10 && num % 10 !== 0 && num % 10 % 2 !== 0){
            let firstDigit = parseInt(num.toString().charAt(0));
            if(firstDigit % 2 !== 0){
                count++;
            }
        }
    }
    return count;
}

