// 1
// Explanation: 
// a = [1, 3, 7, 13, 21]
// The only valid triple is (1, 7, 13).
function get_max_triples(n){
    let count = 0;
    for(let i = 1; i <= n; i++){
        for(let j = i + 1; j <= n; j++){
            for(let k = j + 1; k <= n; k++){
                if((i * i - i + 1) + (j * j - j + 1) + (k * k - k + 1) % 3 === 0){
                    count++;
                }
            }
        }
    }
    return count;
}

