// The only valid triple is (1, 7, 13).
function get_max_triples(n){
    let count = 0;
    let arr = [];
    for(let i = 1; i <= n; i++){
        arr.push(i*i - i + 1);
    }
    for(let i = 0; i < n; i++){
        for(let j = i+1; j < n; j++){
            for(let k = j+1; k < n; k++){
                if((arr[i] + arr[j] + arr[k]) % 3 === 0){
                    count++;
                }
            }
        }
    }
    return count;
}

