//Write a function that accepts an array of strings.
// The array contains different words. Return the word with maximum number
// of unique characters. If multiple strings have maximum number of unique
// characters, return the one which comes first in lexicographical order.
// >>> find_max(["name", "of", "string"])
// "string"
// >>> find_max(["name", "enam", "game"])
// "enam"
// >>> find_max(["aaaaaaa", "bb", "cc"])
// "aaaaaaa"
function find_max(words){
    let max_unique_chars = 0;
    let max_word = '';
    for(let word of words){
        let unique_chars = new Set(word.split('')).size;
        if(unique_chars > max_unique_chars){
            max_unique_chars = unique_chars;
            max_word = word;
        } else if(unique_chars === max_unique_chars){
            max_word = word < max_word ? word : max_word;
        }
    }
    return max_word;
}

